import { useState, useEffect } from "react";
import Select from "react-select";
import axios from "axios";
import "./app.css";

const App = () => {
  const [data, setData] = useState([]);
  const [selectOption, setSelectOption] = useState(null);
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [message, setMessage] = useState("");

  const handlechange = (selectedOption) => {
    setSelectOption(selectedOption);
    setMessage(`You selected: ${selectedOption.label}`);
  };

  const customFilterOption = (label, rawInput) => {
    return option.label.startsWith(rawInput);
  };

  const handleInputChange = (inputValue) => {
    if (inputValue) {
      setIsMenuOpen(true);
    } else {
      setIsMenuOpen(false);
    }
  };

  useEffect(() => {
    getAll();
  }, []);

  const getAll = async () => {
    try {
      const response = await axios.get("http://localhost:5000/option/get");
      const options = response.data.map((item) => ({
        value: item.id, // replace with your unique identifier from the database
        label: item.country,
      }));
      setData(options);
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className="App">
      <h1>Searchable Dropdown</h1>
      <Select
        options={data}
        value={selectOption}
        onChange={handlechange}
        onInputChange={handleInputChange}
        menuIsOpen={isMenuOpen}
        placeholder="Select an option"
        filterOption={customFilterOption}
      />
      {message && <p>{message}</p>}
    </div>
  );
};

export default App;
