import { useState } from "react";
import Select from "react-select"
import "./app.css"
const App = () =>{
  const option = [
    {value:"india",label:"india"},
    {value:"bhutan",label:"bhutan"},
    {value:"china",label:"china"},
    {value:"paksitan",label:"paksitan"},
    {value:"erope",label:"erope"},
    {value:"england",label:"england"},
    {value:"srilanka",label:"srilanka"}


  ]
  const [selectoption,setselectoption] = useState(null)
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const handlechange = (selectoption) =>{
       setselectoption(selectoption)
  }
  const handleInputChange = (inputValue) => {
    if (inputValue) {
      setIsMenuOpen(true);
    } else {
      setIsMenuOpen(false);
    }
  };
  useEffect(() => {
    // Fetch options from the API based on inputValue
    if (inputValue) {
      fetchOptionsFromAPI(inputValue).then((data) => {
        setOptions(data);
      });
    } else {
      setOptions([]); // Clear options when inputValue is empty
    }
  }, [inputValue]);
  return (
    <div class="App">
       <h1>Searchable Dropdown</h1>
       <Select    
          options = {option}
          value={selectoption}
          onChange={handlechange}
          onInputChange={handleInputChange}
          menuIsOpen={isMenuOpen}
          isSearchable={true}
          placeholder="Enter Country Name"
       ></Select> 
    </div>
  );
}

export default App;
