import { useState } from "react";
import Select from "react-select"
import "./app.css"
const App = () =>{
  const option = [
    {value:"india",label:"india"},
    {value:"bhutan",label:"bhutan"},
    {value:"china",label:"china"},
    {value:"paksitan",label:"paksitan"},
    {value:"erope",label:"erope"},
    {value:"england",label:"england"},
    {value:"srilanka",label:"srilanka"},
    {value:"singapore",label:"singapore"},
    {value:"nepal",label:"nepal"}


  ]
  const [selectoption,setselectoption] = useState(null)
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [message, setMessage] = useState("");
  const handlechange = (selectoption) =>{
       setselectoption(selectoption)
       setMessage(`You selected: ${selectoption.label}`);
  }
  const customFilterOption = (option, rawInput) => {
    return option.label.startsWith(rawInput) 
  };
  const handleInputChange = (inputValue) => {
    if (inputValue) {
      setIsMenuOpen(true);
    } else {
      setIsMenuOpen(false);
    }
  };

  return (
    <div className="App">
       <h1>Searchable Dropdown</h1>
       <Select    
          options = {option}
          value={selectoption}
          onChange={handlechange}
          onInputChange={handleInputChange}
          menuIsOpen={isMenuOpen}
          isSearchable={true}
          placeholder="Enter Country Name"
          filterOption={customFilterOption}

       > {message && <p>{message}</p>}</Select> 
    </div>
  );
}

export default App;
