import { useState, useEffect } from "react";
import Select from "react-select";
import axios from "axios";
import "./app.css";

const App = () => {
  const [data, setData] = useState([]);
  const [selectOption, setSelectOption] = useState(null);
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [message, setMessage] = useState("");

  const handlechange = (e) => {
    console.log("handlechange", selectedOption);
    setSelectOption(selectedOption);
    setMessage(`You selected: ${selectedOption.label}`);
  };

  const customFilterOption = (options, rawInput) => {
    console.log("option:",options,"rawinput:",rawInput);
    return options.label.startsWith(rawInput);

  };

  const handleInputChange = (inputValue) => {
    console.log("inputvalue:",inputValue);
    if (inputValue) {
      setIsMenuOpen(true);
    } else {
      setIsMenuOpen(false);
    }
  };

  useEffect(() => {
    console.log("useffect called");
    getAll();
  }, []);

  const getAll = async () => {
    try {
      const response = await axios.get("http://localhost:5000/option/get");
      const options = response.data.map((item) => ({
        value: item.id, // replace with your unique identifier from the database
        label: item.country,
      }));

      setData(options);
      console.log("while getting data:",options); 
    } catch (error) { 
      console.log(error);
    }
  };

  return (
    <div className="App">
      <h1>Searchable Dropdown</h1>
      <Select
        options={data}
        value={selectOption}
        onChange={handlechange}
        onInputChange={handleInputChange}
        menuIsOpen={isMenuOpen}
        placeholder="Select an option"
        filterOption={customFilterOption}
      />
      <p>{message}</p>
    </div>
  );
};

export default App;
