import { useState } from "react";
import Select from "react-select"
import "axios"
import "./app.css"
import axios from "axios";
const App = () =>{
  const[data,setdata] = useState("")
  const [selectoption,setselectoption] = useState(null)
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [message, setMessage] = useState("");
  const handlechange = (selectoption) =>{
       setselectoption(selectoption)
       setMessage(`You selected: ${selectoption.label}`);

  }
  const customFilterOption = (data, rawInput) => {
    return data.label.startsWith(rawInput) 
  };
  const handleInputChange = (inputValue) => {
    if (inputValue) {
      setIsMenuOpen(true);
      getAll();

    } else {
      setIsMenuOpen(false);
    }
  };

  const getAll = async () => {
    try {
      const response = await axios.get("http://localhost:5000/option/get");
      console.log();
      setdata(response);
    } catch (error) {
      // Handle error
      console.log(error);
    }
  };


  return (
    <div className="App">
       <h1>Searchable Dropdown</h1>
       <Select    
          data = {data}
          value={selectoption}
          onChange={handlechange}
          onInputChange={handleInputChange}
          menuIsOpen={isMenuOpen}
          isSearchable={true}
          placeholder="Select an option"
          filterOption={customFilterOption}



       /> 
       {message && <p>{message}</p>}
    </div>
  );
}

export default App;
